import React, { useState } from 'react';

const TextCounter = () => {
    const [text, setText] = useState('');
    const [cost, setCost] = useState(0);

    const handleChange = (event) => {
        const value = event.target.value;
        setText(value);
        calculateCost(value.length);
    }

    const calculateCost = (charCount) => {
        let totalCost = 0;
        if (charCount <= 1000) {
            totalCost = 50;
        } else {
            totalCost = 50 + ((charCount - 1000) * 0.5);
        }
        setCost(totalCost);
    }

    return (
        <div>
      <textarea
          value={text}
          onChange={handleChange}
      />
            <p>Cost: {cost}$</p>
        </div>
    );
}

export default TextCounter;
