import React, { useState } from 'react';
import './MyCheckbox.css';

const MyCheckbox = () => {
    const [isChecked, setIsChecked] = useState(false);
    const [selectedValue, setSelectedValue] = useState("Кнопка-чекбокс"); // Начальное значение кнопки

    const handleButtonClick = () => {
        setIsChecked(!isChecked);
    };

    const handleListOptionClick = (value) => {
        setSelectedValue(value);
        setIsChecked(false); // Закрываем список после выбора значения
    };

    return (
        <div className="button-checkbox">
            <button
                className={`custom-button ${isChecked ? 'checked' : ''}`}
                onClick={handleButtonClick}
                type="button"
            >
                {selectedValue}
            </button>
            <div className={`hidden-list ${isChecked ? 'active' : ''}`}>
                <div onClick={() => handleListOptionClick("Значение 1")}>Значение 1</div>
                <div onClick={() => handleListOptionClick("Значение 2")}>Значение 2</div>
                <div onClick={() => handleListOptionClick("Значение 3")}>Значение 3</div>
            </div>
        </div>
    );
};

export default MyCheckbox;
