import './MyArea.css';
import React, { useState } from 'react';

const MyArea = () => {
    const [text, setText] = useState('');
    const [selectedFile, setSelectedFile] = useState(null);

    const handleTextChange = (event) => {
        setText(event.target.value);
    };

    const handleFileChange = (event) => {
        const file = event.target.files[0];
        if (file) {
            setSelectedFile(file);

            const reader = new FileReader();
            reader.onload = (event) => {
                const fileContent = event.target.result;
                setText(fileContent);

                // Убираем текст в label и span
                document.querySelector('.placeholder').style.display = 'none';
                document.querySelector('.area-label').style.display = 'none';
            };
            reader.readAsText(file);
        } else {
            setSelectedFile(null);
            setText('');

            // Возвращаем текст в label и span
            document.querySelector('.placeholder').style.display = 'inline';
            document.querySelector('.area-label').style.display = 'inline';
        }
    };

    return (
        <div className="area">
      <textarea
          className="area-text"
          value={text}
          onChange={handleTextChange}
      ></textarea>
            <div className="area-download">
                <span className="placeholder">Введіть текст або </span>
                <label className="area-label">
                    Завантажте файл
                    <input
                        className="area-input"
                        type="file"
                        onChange={handleFileChange}
                    />
                </label>
            </div>
        </div>
    );
};

export default MyArea;
