import "./styles/App.css";
import React, {useState} from "react";

// import {useState} from "react";


function App() {

    const [isActive, setIsActive] = useState(false);

    const handleClick = () => {
        setIsActive(!isActive);
    };

    return (
        <div className="App">
            <main className="main_new">
                <form className="section_new makeOrder">
                    <div className="makeOrder_item inputs">
                        <h3 className="medium_title">Замовити переклад або редагування</h3>
                        <fieldset className="selector false service" tabIndex="0">
                            <button tabIndex="0" type="button" className={`toggle-button ${isActive ? 'active' : 'inactive'}`} onClick={handleClick}>
                                Послуга
                            </button>
                        </fieldset>
                    </div>
                </form>
            </main>
        </div>
    )
}

export default App;
