import React, {useRef, useState} from "react";
import "./styles/App.css";
import MyCounter from "./dev/components/Counter";
import MyPostItem from "./dev/components/PostItem";
import MyInput from "./dev/components/UI/input/MyInput";
import PostList from "./dev/components/PostList";
import MyButton from "./dev/components/UI/button/MyButton";
import PostForm from "./dev/components/PostForm";
import MySelect from "./dev/components/UI/select/MySelect";

function App() {

    const [posts, setPosts] = useState([
        {id: 1, title: 'JavaScript', body: 'Description'},
        {id: 2, title: 'Python', body: 'Description'},
        {id: 3, title: 'Java', body: 'Description'},
    ])

    const [selectedSort, setSelectedSort] = useState('')
    const createPost = (newPost) => {
        setPosts([...posts, newPost])
    }

    const removePost = (post) => {
        setPosts(posts.filter(p => p.id !== post.id))
    }

    const sortPosts = (sort) => {
        setSelectedSort(sort);
        setPosts([...posts].sort((a,b) => a[sort].localeCompare(b[sort])))
    }

    return (
        <div className="App">
            <PostForm create={createPost}/>
            <hr style={{margin: '15px 0'}}/>
            <div>
                <MySelect
                    value={selectedSort}
                    onChange={sortPosts}
                    defaultValue="Sorted by:"
                    option={[
                    {value: 'title', name: 'by title'},
                    {value: 'body', name: 'by description'},
                ]}/>
            </div>
            {posts.length
                ? <PostList remove={removePost} posts={posts} title='List Posts One'/>
                : <h1 style={{textAlign: 'center'}}>Post not found</h1>}
        </div>);
}

export default App;
