import React, { useState } from 'react';
import './MyCheckbox.css';

const MyCheckbox = () => {
    const [isChecked, setIsChecked] = useState(false);

    const handleButtonClick = () => {
        setIsChecked(!isChecked);
    };

    return (
        <div className="button-checkbox">
            <button
                className={`custom-button ${isChecked ? 'opener' : ''}`}
                onClick={handleButtonClick}
                type="button"
            >
                <label>Послуга</label>
            </button>
            <div className={`hidden-list ${isChecked ? 'opener' : ''}`}>
                <label htmlFor="">Редагування</label>
                <label htmlFor="">Переклад</label>
            </div>
        </div>
    );
};

export default MyCheckbox;
