import React, { useState } from 'react';
import styled from 'styled-components';

const ButtonCheckbox = styled.div`
  display: inline-block;
  position: relative;
`;

const HiddenList = styled.div`
  display: ${props => (props.checked ? 'block' : 'none')};
  padding: 10px;
  border: 1px solid #ccc;
  border-radius: 5px;
  margin-top: 10px;
`;

const CustomButton = styled.button`
  padding: 10px 20px;
  border: 1px solid #ccc;
  border-radius: 5px;
  cursor: pointer;
  background-color: ${props => (props.checked ? '#00f' : 'transparent')};
  color: ${props => (props.checked ? '#fff' : '#000')};
`;

const MyCheckbox = () => {
    const [isChecked, setIsChecked] = useState(false);

    const handleButtonClick = () => {
        setIsChecked(!isChecked);
    };

    return (
        <ButtonCheckbox>
            <CustomButton onClick={handleButtonClick} checked={isChecked}>
                Кнопка-чекбокс
            </CustomButton>
            <HiddenList checked={isChecked}>Скрытый список</HiddenList>
        </ButtonCheckbox>
    );
};

export default MyCheckbox;
