import React from "react";
import {useState} from "react";



function App() {

    const [isActive, setIsActive] = useState(false); // Изначально кнопка не активна
    // const [editingText, setEditingText] = useState('Редагування')
    // const [translationText, setTranslationText] = useState('Переклад')
    const handleButtonClick = () => {
        setIsActive(!isActive); // Инвертируем состояние активности
    };

    const buttonClass = isActive ? 'active' : 'inactive';

    return (
        <div className="App">
            <button className={`toggle-button ${buttonClass}`} type="button" onClick={handleButtonClick}>
                <label></label>
            </button>
            <button className="menu_section" type="button">
                <label>
                    Мова
                </label>
            </button>
        </div>
    )
}

export default App;
