import React, {useState} from 'react';
import './MyArea.css';

const MyArea = () => {

    const [defaultTextArea, setDefaultTextArea] = useState("Введіть текст або");

    const handleTextChange = (event) => {
        setDefaultTextArea(event.target.value);
    };

    return (
        <div className="area">
            <textarea
                className="area-text"
                value={defaultTextArea}
                onChange={handleTextChange}
            >
            </textarea>
        </div>
    );
};

export default MyArea;
