import React, {useMemo, useRef, useState} from "react";
import "./styles/App.css";
import MyCounter from "./dev/components/Counter";
import MyPostItem from "./dev/components/PostItem";
import MyInput from "./dev/components/UI/input/MyInput";
import PostList from "./dev/components/PostList";
import MyButton from "./dev/components/UI/button/MyButton";
import PostForm from "./dev/components/PostForm";
import MySelect from "./dev/components/UI/select/MySelect";
import PostFilter from "./dev/components/PostFilter";
import MyModal from "./dev/components/UI/MyModal/MyModal";

function App() {

    const [posts, setPosts] = useState([
        {id: 1, title: 'JavaScript', body: 'Description per JavaScript'},
        {id: 2, title: 'Python', body: 'Description per Python'},
        {id: 3, title: 'Java', body: 'Description per Java'},
    ])

    const [filter, setFilter] = useState({sort: '', query: ''})

    const [modal, setModal] = useState(false);

    const sortedPosts = useMemo(() => {
        if (filter.sort) {
            return [...posts].sort((a, b) => a[filter.sort].localeCompare(b[filter.sort]))
        }
        return posts;
    }, [filter.sort, posts]);

    const sortedAndSearchedPosts = useMemo(() => {
        return sortedPosts.filter(post => post.title.toLowerCase().includes(filter.query.toLowerCase()))
    }, [filter.query, sortedPosts])
    const createPost = (newPost) => {
        setPosts([...posts, newPost])
    }
    const removePost = (post) => {
        setPosts(posts.filter(p => p.id !== post.id))
    }

    return (
        <div className="App">
            <MyButton onClick={() => setModal(true)}>
                Create post
            </MyButton>
            <MyModal visible={modal} setVisible={setModal}>
                <PostForm create={createPost}/>
            </MyModal>
            <hr style={{margin: '15px 0'}}/>
            <PostFilter
                filter={filter}
                setFilter={setFilter}
            />
            <PostList remove={removePost} posts={sortedAndSearchedPosts} title='List Posts One'/>
        </div>);
}

export default App;
