import "./styles/App.css";
import React from "react";
import {useState} from "react";



function App() {

    const [isActive, setIsActive] = useState(false); // Изначально кнопка не активна
    const [isListVisible, setIsListVisible] = useState(false);
    // const [editingText, setEditingText] = useState('Редагування')
    // const [translationText, setTranslationText] = useState('Переклад')
    const handleButtonClick = () => {
        setIsActive(!isActive); // Инвертируем состояние активности
        setIsListVisible(!isListVisible);
    };

    const [likes, setLikes] = useState()

    const updateLikes = () => {
      setLikes(likes+1)
    }

    const buttonClass = isActive ? 'Актив' : 'Не актив';

    return (
        <div className="App">
            <h1>0</h1>
            <button className="menu_section" type="button">
                <label className={`toggle-button ${buttonClass}`} onClick={handleButtonClick}>{buttonClass}</label>
            </button>
            {isListVisible && (
                <ul className="selector_list">
                    <button onClick={updateLikes} type="button">
                        Редагування
                    </button>
                    <button>
                        Переклад
                    </button>
                </ul>
            )}
            <div>
                <button>
                    <label htmlFor="">
                        что то будет написано
                    </label>
                </button>
            </div>
        </div>
    )
}

export default App;
