import "./styles/App.css";
import React, {useEffect, useRef, useState} from "react";

function App() {
    const containerRef = useRef(null);

    const [openedService, setOpenedService] = useState(false)
    const [defaultServiceOption, setDefaultServiceOption] = useState('Послуга')
    const [optionColor, setOptionColor] = useState('');


    const [openedValueLanguage, setOpenedValueLanguage] = useState(false)
    const [defaultValueLanguage, setDefaultValueLanguage] = useState('Мова')

    const clickBtnOpenerLanguage = () => { // Открытие и закрытие выбора услуги
        setOpenedService(!openedService)
    }
    const clickBtnOpenerLanguageOption = () => { // Открытие и закрытие выбора опции для перевода
        setOpenedValueLanguage(!openedValueLanguage)
    }


    const variableService = (value, color) => { // Применения выбранного сервиса как замена стандартному значению и смена более цвета для выбранного значения
        setDefaultServiceOption(value) // Смена значение
        setOptionColor(color) // Смена цвета
        setOpenedService(!openedService) // Авто закрытие выпадающего списка
    }

    const handleClickOutside = (event) => { // Слушатель события на клик вне области контейнера и перевод контейнера в закрытое состояние
        if (containerRef.current && !containerRef.current.contains(event.target)) {
            setOpenedService(false);
            setOpenedValueLanguage(false)
        }
    };

    useEffect(() => {
        document.addEventListener('click', handleClickOutside); // добавляет слушатель события клика на весь документ, и при каждом клике вызывается функция
        return () => {
            document.removeEventListener('click', handleClickOutside); // удаляет слушатель при демонтировании компонента, чтобы избежать утечек памяти и ненужных вызовов после того, как компонент больше не отображается.
        };
    }, []);


    return (
        <main className="main_new">
            <form className="section_new makeOrder">
                <div className="makeOrder_item inputs">
                    <h3 className="medium_title">Замовити переклад або редагування</h3>
                    <label className="box-btn">
                        <button
                            className={`btn one ${openedService ? 'opener' : ''} ${optionColor}`}
                            onClick={clickBtnOpenerLanguage}
                            type="button"
                            ref={containerRef}
                        >
                            <label className="position-element">
                                {defaultServiceOption}
                                <img src="/arrow_down.svg" alt="arrow-down"/>
                            </label>
                        </button>
                        <div className={`hidden-list ${openedService ? 'opened' : ''}`}>
                            <div className="position-list-option">
                                <label onClick={() => variableService('Редагування', 'color')}>Редагування</label>
                                <label onClick={() => variableService('Переклад', 'color')}>Переклад</label>
                            </div>
                        </div>
                        <button className={`btn two ${openedValueLanguage ? 'opener' : ''} ${optionColor}`}
                                onClick={clickBtnOpenerLanguageOption}
                                type="button"
                        >
                            <label className="position-element">
                                {defaultValueLanguage}
                                <img src="/arrow_down.svg" alt="arrow-down"/>
                            </label>
                        </button>
                    </label>
                </div>
                <div className="makeOrder_item">
                </div>
            </form>
        </main>
    )
}

export default App;
