import "./styles/App.css";
import MyCheckbox from "./components/MyCheckbox/MyCheckbox";
import MyArea from "./components/MyArea/MyArea";
import {MyClipBoard} from "./components/MyClipBoard/MyClipBoard";
import React, {useState} from "react";

function App() {
    const [selectedColor, setSelectedColor] = useState('');

    const [openedService, setOpenedService] = useState(false)
    const [defaultServiceOption, setDefaultServiceOption] = useState('Послуга')

    const [openedValueLanguage, setOpenedValueLanguage] = useState(false)
    const [defaultValueLanguage, setDefaultValueLanguage] = useState('Мова')

    const clickBtnOpenerLanguage = () => {
        setOpenedService(!openedService)
    }
    const clickBtnOpenerLanguageOption = () => {
        setOpenedValueLanguage(!openedValueLanguage)
    }


    const variableService = (value, color) => {
        setDefaultServiceOption(value)
        setSelectedColor(color)
        setOpenedService(!openedService)
    }


    return (
        <main className="main_new">
            <form className="section_new makeOrder">
                <div className="makeOrder_item inputs">
                    <h3 className="medium_title">Замовити переклад або редагування</h3>
                    <label className="box-btn">
                        <button className={`btn ${openedService ? 'opener' : ''} ${selectedColor}`} type="button" onClick={clickBtnOpenerLanguage}>
                            <label className="position-element">
                                {defaultServiceOption}
                                <img src="/arrow_down.svg" alt="arrow-down"/>
                            </label>
                        </button>
                        <div className={`hidden-list ${openedService ? 'opened' : ''}`}>
                            <div className="position-list">
                                <label onClick={() => variableService('Редагування', '#000000FF')}>Редагування</label>
                                <label onClick={() => variableService('Переклад', '#000000FF')}>Переклад</label>
                            </div>
                        </div>
                        <button className={`btn ${openedValueLanguage ? 'opener' : ''} ${selectedColor}`} type="button" onClick={clickBtnOpenerLanguageOption}>
                            <label className="position-element">
                                {defaultValueLanguage}
                                <img src="/arrow_down.svg" alt="arrow-down"/>
                            </label>
                        </button>
                    </label>
                </div>
                <div className="makeOrder_item">
                </div>
            </form>
        </main>
    )
}

export default App;
