import React, {useState} from 'react';
import './MyCheckbox.css';

const MyCheckbox = () => {
    const [isChecked, setIsChecked] = useState(false);
    const [edit, setEdit] = useState('Редагування');

    const handleButtonClick = () => {
        setIsChecked(!isChecked);
    };

    return (
        <div className="button-checkbox">
            <button
                className={`custom-button ${isChecked ? 'checked' : ''}`}
                onClick={handleButtonClick}
                type="button"
            >
                <label>Послуга</label>
            </button>
            <div className={`hidden-list ${isChecked ? 'active' : ''}`}>
                <div className="position-list">
                    <label onClick={() => setEdit('Редагування')}>Редагування</label>
                    <label htmlFor="">Переклад</label>
                </div>
            </div>
        </div>
    );
};

export default MyCheckbox;
