import "./styles/App.css";
import React from "react";
import {useState} from "react";



function App() {

    const [isActive, setIsActive] = useState(false); // Изначально кнопка не активна
    const [isListVisible, setIsListVisible] = useState(false);
    // const [editingText, setEditingText] = useState('Редагування')
    // const [translationText, setTranslationText] = useState('Переклад')
    const handleButtonClick = () => {
        setIsActive(!isActive); // Инвертируем состояние активности
        setIsListVisible(!isListVisible);
    };

    const buttonClass = isActive ? 'active' : 'inactive';

    return (
        <div className="App">
            <button className="menu_section" type="button" onClick={handleButtonClick}>
                <label className={`toggle-button ${buttonClass}`}>Послуги</label>
                {isListVisible && (
                    <div>
                        <>Редагування</>
                        <li>Переклад</li>
                    </div>
                )}
            </button>
            <button className="menu_section" type="button">
                <label>
                    Мова
                </label>
            </button>
        </div>
    )
}

export default App;
