import React, {useState} from 'react';
import './MyCheckbox.css';

const MyCheckbox = () => {
    const [isChecked, setIsChecked] = useState(false);
    const [edit, setEdit] = useState('Редагування');

    const handleButtonClick = () => {
        setIsChecked(!isChecked);
    };


    let service = 'Послуга';
    const handleEditButtonClick = () => {
        service = edit
        setEdit(() => service)
        console.log('что то =')
    }

    return (
        <fieldset>
            <div className="button-checkbox">
                <button
                    className={`custom-button ${isChecked ? 'checked' : ''}`}
                    onClick={handleButtonClick}
                    type="button"
                >
                    <label>{service}</label>
                </button>
                <div className={`hidden-list ${isChecked ? 'active' : ''}`}>
                    <div className="position-list">
                        <label onClick={handleEditButtonClick}>Редагування</label>
                        <label htmlFor="">Переклад</label>
                    </div>
                </div>
            </div>
        </fieldset>
    );
};

export default MyCheckbox;
