import React, {useState} from 'react';
import './MyArea.css';

const MyArea = () => {
    return (
        <div className="area">
            <textarea className="area-text"></textarea>
            <div className="area-download">
                <span className="placeholder">Введіть текст або </span>
                <label>
                    завантажити файл
                    <input className="input" type="file"/>
                </label>
            </div>
        </div>
    );
};

export default MyArea;
