import React, { useState } from 'react';
import styled from 'styled-components';

const CheckboxButton = styled.div`
  display: inline-block;
  position: relative;
`;

const HiddenList = styled.div`
  display: ${props => (props.checked ? 'block' : 'none')};
  padding: 10px;
  border: 1px solid #ccc;
  border-radius: 5px;
  margin-top: 10px;
`;

const CheckboxLabel = styled.label`
  display: inline-block;
  padding: 10px 20px;
  border: 1px solid #ccc;
  border-radius: 5px;
  cursor: pointer;
  border: 1px solid ${props => (props.checked ? '#00f' : '#ccc')};
`;

const MyCheckbox = () => {
    const [isChecked, setIsChecked] = useState(false);

    const handleCheckboxChange = () => {
        setIsChecked(!isChecked);
    };

    return (
        <CheckboxButton>
            <input type="checkbox" id="myCheckbox" checked={isChecked} onChange={handleCheckboxChange} />
            <CheckboxLabel htmlFor="myCheckbox" checked={isChecked}>
                Кнопка-чекбокс
            </CheckboxLabel>
            <HiddenList checked={isChecked}>Скрытый список</HiddenList>
        </CheckboxButton>
    );
};

export default MyCheckbox;
