import "./styles/App.css";
import React, {useState} from "react";
// import {useState} from "react";



function App() {

    const [isActive, setIsActive] = useState(false); // Изначально кнопка неактивна
    const [selectedOption, setSelectedOption] = useState(null); // Изначально не выбран ни один пункт

    const toggleButtonState = () => {
        setIsActive(!isActive);
    };

    const handleOptionSelect = (option) => {
        setSelectedOption(option);
        setIsActive(true); // После выбора пункта активируем кнопку
    };


    return (
        <div className="App">
            <main className="main_new">
                <form className="section_new makeOrder">
                    <div className="makeOrder_item inputs">
                        <h3 className="medium_title">Замовити переклад або редагування</h3>
                        <fieldset className="selector false service" tabIndex="0">
                        </fieldset>
                    </div>
                </form>
            </main>
        </div>
    )
}

export default App;
