import "./styles/App.css";
import React, {useEffect, useRef, useState } from "react";

function App() {
    const containerRefOption = useRef(null);
    const containerRefLanguage = useRef(null);

    const [openedService, setOpenedService] = useState(false);
    const [defaultServiceOption, setDefaultServiceOption] = useState('Послуга');
    const [optionColor, setOptionColor] = useState('');

    const [openedValueLanguage, setOpenedValueLanguage] = useState(false);
    const [defaultValueLanguage, setDefaultValueLanguage] = useState('Мова');
    const [valueLanguageColor, setValueLanguageColor] = useState('');

    const [firstButtonSelected, setFirstButtonSelected] = useState(false);

    const clickBtnOpenerLanguage = () => {
        setOpenedService(!openedService);
    };

    const clickBtnOpenerLanguageOption = () => {
        if (firstButtonSelected) {
            setOpenedValueLanguage(!openedValueLanguage);
        }
    };

    const variableService = (value, color) => {
        setDefaultServiceOption(value);
        setOptionColor(color);
        setOpenedService(!openedService);
        setFirstButtonSelected(true); // Устанавливаем, что первая кнопка была выбрана
    };

    const valueLanguage = (value, color) => {
        setDefaultValueLanguage(value);
        setValueLanguageColor(color);
        setOpenedValueLanguage(!openedValueLanguage);
    };

    const handleClickOutsideOption = (event) => {
        if (containerRefOption.current && !containerRefOption.current.contains(event.target)) {
            setOpenedService(false);
        }
    };

    useEffect(() => {
        document.addEventListener('click', handleClickOutsideOption);
        return () => {
            document.removeEventListener('click', handleClickOutsideOption);
        };
    }, []);


    const handleClickOutsideLanguage = (event) => {
        if (containerRefLanguage.current && !containerRefLanguage.current.contains(event.target)) {
            setOpenedValueLanguage(false);
        }
    }

    useEffect(() => {
        document.addEventListener('click', handleClickOutsideLanguage);
        return () => {
            document.removeEventListener('click', handleClickOutsideLanguage);
        };
    }, []);


    return (
        <main className="main_new">
            <form className="section_new makeOrder">
                <div className="makeOrder_item inputs">
                    <h3 className="medium_title">Замовити переклад або редагування</h3>
                    <label className="box-btn">
                        <button
                            className={`btn one ${openedService ? 'opener' : ''} ${optionColor}`}
                            onClick={clickBtnOpenerLanguage}
                            type="button"
                            ref={containerRefOption}
                        >
                            <label className="position-element">
                                {defaultServiceOption}
                                <img src="/arrow_down.svg" alt="arrow-down" />
                            </label>
                        </button>
                        <div className={`hidden-list-option ${openedService ? 'opened' : ''}`}>
                            <div className="position-list-option">
                                <label onClick={() => variableService('Редагування', 'color')}>Редагування</label>
                                <label onClick={() => variableService('Переклад', 'color')}>Переклад</label>
                            </div>
                        </div>
                        <button
                            className={`btn two ${openedValueLanguage ? 'opener' : ''} ${valueLanguageColor} ${!firstButtonSelected ? 'disabled' : ''}`}
                            onClick={clickBtnOpenerLanguageOption}
                            type="button"
                            disabled={!firstButtonSelected} // Делаем вторую кнопку неактивной, если первая не выбрана
                            ref={containerRefLanguage}
                        >
                            <label className="position-element">
                                {defaultValueLanguage}
                                <img src="/arrow_down.svg" alt="arrow-down" />
                            </label>
                        </button>
                        <div className={`hidden-list-value-language ${openedValueLanguage ? 'opener' : ''}`}>
                            <div className="position-list-value-language">
                                <label onClick={() => valueLanguage('Українська', 'color')}>Українська</label>
                                <label onClick={() => valueLanguage('Російська', 'color')}>Російська</label>
                                <label onClick={() => valueLanguage('Англійська', 'color')}>Англійська</label>
                                <label onClick={() => valueLanguage('Англійська (носій)', 'color')}>Англійська (носій)</label>
                            </div>
                        </div>
                    </label>

                </div>
                <div className="makeOrder_item"></div>
            </form>
        </main>
    );
}

export default App;
