import React, { useState } from 'react';
import './MyCheckbox.css';

const MyCheckbox = () => {
    const [selectedValue1, setSelectedValue1] = useState("Кнопка-чекбокс 1");
    const [selectedColor1, setSelectedColor1] = useState("");
    const [selectedValue2, setSelectedValue2] = useState("Кнопка-чекбокс 2");
    const [selectedColor2, setSelectedColor2] = useState("");
    const [isList1Open, setIsList1Open] = useState(false);
    const [isList2Open, setIsList2Open] = useState(false);

    const handleButtonClick1 = () => {
        setIsList1Open(!isList1Open);
    };

    const handleButtonClick2 = () => {
        setIsList2Open(!isList2Open);
    };

    const handleListOptionClick1 = (value, color) => {
        setSelectedValue1(value);
        setSelectedColor1(color);
        setIsList1Open(false);
        setIsList2Open(false);
    };

    const handleListOptionClick2 = (value, color) => {
        setSelectedValue2(value);
        setSelectedColor2(color);
        setIsList2Open(false);
    };

    const options1 = ["Значение 1", "Значение 2"];
    const options2 = selectedValue1 === "Значение 1" || selectedValue1 === "Значение 2"
        ? ["Значение 3", "Значение 4"]
        : ["Значение 5", "Значение 6"]; 
    return (
        <div className="button-container">
            <div className="button-checkbox">
                <button
                    className={`custom-button ${isList1Open ? 'checked' : ''} ${selectedColor1}`}
                    onClick={handleButtonClick1}
                    type="button"
                >
                    {selectedValue1}
                </button>
                <div className={`hidden-list ${isList1Open ? 'active' : ''}`}>
                    {options1.map((option, index) => (
                        <div key={index} onClick={() => handleListOptionClick1(option, "checked")}>
                            {option}
                        </div>
                    ))}
                </div>
            </div>

            <div className="button-checkbox">
                <button
                    className={`custom-button ${isList2Open ? 'checked' : ''} ${selectedColor2}`}
                    onClick={handleButtonClick2}
                    disabled={selectedValue1 !== "Значение 1"}
                    type="button"
                >
                    {selectedValue2}
                </button>
                <div className={`hidden-list ${isList2Open ? 'active' : ''}`}>
                    {options2.map((option, index) => (
                        <div key={index} onClick={() => handleListOptionClick2(option, "checked")}>
                            {option}
                        </div>
                    ))}
                </div>
            </div>
        </div>
    );
};

export default MyCheckbox;
