import React, {useRef, useState} from "react";
import "./styles/App.css";
import MyCounter from "./dev/components/Counter";
import MyPostItem from "./dev/components/PostItem";
import MyInput from "./dev/components/UI/input/MyInput";
import PostList from "./dev/components/PostList";
import MyButton from "./dev/components/UI/button/MyButton";

function App() {

    const [posts, setPosts] = useState([
        {id: 1, title: 'JavaScript', body: 'Description'},
        {id: 2, title: 'Python', body: 'Description'},
        {id: 3, title: 'Java', body: 'Description'},
    ])

    // const bodyInputRef = useRef();

    const [title, setTitle] = useState('')
    const [body, setBody] = useState('')
    const addNewPost = (e) => {
        e.preventDefault();
        const newPost = {
            id: Date.now(),
            title,
            body,
        }
        setPosts([...posts, newPost])
        setTitle('')
        setBody('')
    }

    return (
        <div className="App">
            <form action="">
                <MyInput value={title} onChange={e => setTitle(e.target.value)} type="text" placeholder="Name post"/>
                <MyInput value={body} onChange={e => setBody(e.target.value)} type="text" placeholder="Description post"/>
                <MyButton onClick={addNewPost}>create post</MyButton>
            </form>
            <PostList posts={posts} title='List Posts One'/>
        </div>);
}

export default App;
