import "./styles/App.css";
import React, {useEffect, useRef, useState} from "react";
import {useText} from "./components/TextContext/TextContext";

function App() {
// Data for operation of the first button responsible for service selection ( данные для работа первой кнопки которая отвечает за выбор услуги )
    const [isOpenedService, setIsOpenedService] = useState(false);
    const [selectedValue, setSelectedValue] = useState("Послуга"); // Начальное значение кнопки
    const [selectedColor, setSelectedColor] = useState(""); // Цвет для выбранного значения
    const [label, setLabel] = useState('')
    const serviceRef = useRef(null);

    const [isOpenedLanguage, setIsOpenedLanguage] = useState(false);
    const [selectedLanguage, setSelectedLanguage] = useState("Мова"); // Начальное значение кнопки
    const [valueLanguageColor, setValueLanguageColor] = useState('');
    const languageRef = useRef(null);

    const [firstButtonSelected, setFirstButtonSelected] = useState(false);
    const [selectedServiceOption, setSelectedServiceOption] = useState('');

    const handleButtonClickService = () => { // Активация кнопки
        setIsOpenedService(!isOpenedService);
    };
    const handleButtonClickLanguage = () => { // Активация кнопки
        if (firstButtonSelected) {
            setIsOpenedLanguage(!isOpenedLanguage);
        }
    };

    const handleListOptionClickService = (value, color) => { // Смена параметров кнопки Услуги
        setSelectedValue(value);
        setSelectedColor(color);
        setIsOpenedService(false);
        setFirstButtonSelected(true);
        setSelectedServiceOption(value);
        setLabel('Послуга')

        if (value === 'Переклад') {
            setSelectedLanguage('Мовна пара');
        }
    };

    const getLanguageOptions = () => {
        switch (selectedServiceOption) {
            case 'Редагування':
                return (
                    <div className="position-list-value-language">
                        <label onClick={() => handleListOptionClickService('Українська', 'color')}>Українська</label>
                        <label onClick={() => handleListOptionClickService('Російська', 'color')}>Російська</label>
                        <label onClick={() => handleListOptionClickService('Англійська', 'color')}>Англійська</label>
                        <label onClick={() => handleListOptionClickService('Англійська (носій)', 'color')}>Англійська (носій)</label>
                    </div>
                );
            case 'Переклад':
                return (
                    <div className="position-list-value-language">
                        <label onClick={() => handleListOptionClickService('Українська', 'color')}>Українська</label>
                        <label onClick={() => handleListOptionClickService('Російська', 'color')}>Російська</label>
                    </div>
                );
            default:
                return null;
        }
    };

    const handleListOptionClickLanguage = (value, color) => {
        setSelectedLanguage(value)
        setValueLanguageColor(color)
        setIsOpenedLanguage(!isOpenedLanguage)
    }

        const handleClickOutside = (event) => { // Отслеживание где был произведен клик
        if (serviceRef.current && !serviceRef.current.contains(event.target)) {
            setIsOpenedService(false);
        }
        if (languageRef.current && !languageRef.current.contains(event.target)) {
            setIsOpenedService(false);
        }
    };

    useEffect(() => { // Закрытие кнопки, при нажатии вне области объекта
        document.addEventListener('click', handleClickOutside);
        return () => {
            document.removeEventListener('click', handleClickOutside);
        };
    }, []);
// ------------------------------------------------------------------------------------------------------------------ //
    const { text, setTextValue } = useText();
    const [selectedFile, setSelectedFile] = useState(null);
    const [isInputActive, setInputActive] = useState(false);
    const [charCount, setCharCount] = useState(0);
    const [isCharCounterVisible, setCharCounterVisible] = useState(false);


    const updateCharCount = (text) => {
        // Убираем пробелы из текста перед подсчетом символов
        setCharCount(text.length);
    };

    const countTextChange = (event) => {
        const newText = event.target.value;
        setTextValue(newText);
        updateCharCount(newText);

        // Проверка, чтобы показать счетчик символов только после первого введенного символа
        if (newText.length > 0) {
            setCharCounterVisible(true);
        } else {
            setCharCounterVisible(false);
        }
    };

    const handleTextChange = (event) => {
        setTextValue(event.target.value);
        if (event.target.value.length > 0) {
            setInputActive(true);
        } else {
            setInputActive(false);
        }
    };

    const handleFileChange = (event) => {
        const file = event.target.files[0];
        if (file) {
            setSelectedFile(file);
            const reader = new FileReader();
            reader.onload = (event) => {
                const fileContent = event.target.result;
                setTextValue(fileContent);
                setInputActive(true);
                setCharCounterVisible(true); // Показать счетчик символов после загрузки файла
            };
            reader.readAsText(file);
        } else {
            setSelectedFile(null);
            setTextValue('');
            setInputActive(false);
            setCharCounterVisible(false); // Скрыть счетчик символов при удалении файла
        }
    };


    useEffect(() => {
        // Подсчет символов сразу после загрузки файла
        if (selectedFile) {
            const reader = new FileReader();
            reader.onload = (event) => {
                const fileContent = event.target.result;
                updateCharCount(fileContent);
            };
            reader.readAsText(selectedFile);
        }
    }, [selectedFile]);
// ------------------------------------------------------------------------------------------------------------------ //



// ------------------------------------------------------------------------------------------------------------------ //


    return (
        <main className="main_new">
            <form className="section_new makeOrder">
                <div className="makeOrder_item inputs">
                    <h3 className="medium_title">Замовити переклад або редагування</h3>
{/* First service selection button ( первая кнопка выбора услуги ) ------------------------------------------------- */}
                    <fieldset>
                        <legend className={`${isOpenedService ? 'checked' : ''}`}>{label}</legend>
                        <div className="button-checkbox" ref={serviceRef}>
                            <button
                                className={`custom-button ${isOpenedService ? 'checked' : ''} ${selectedColor}`}
                                onClick={handleButtonClickService}
                                type="button"
                            >
                                <label>
                                    {selectedValue}
                                    <img alt="icon" src="/arrow_down.svg"/>
                                </label>
                            </button>
                            <div className={`hidden-list ${isOpenedService ? 'active' : ''}`}>
                                <div className="position-list">
                                    <label onClick={() => handleListOptionClickService('Редагування', "color")}>Редагування</label>
                                    <label onClick={() => handleListOptionClickService('Переклад', "color")}>Переклад</label>
                                </div>
                            </div>
                        </div>
                    </fieldset>
{/* ---------------------------------------------------------------------------------------------------------------- */}
                    <div className="area">
            <textarea
                className="area-text"
                value={text}
                onChange={handleTextChange}
                onKeyUp={countTextChange}
            ></textarea>
                        {isCharCounterVisible && (
                            <div className="char-count">{charCount} символів</div>
                        )}
                        <div className="area-download">
                            {isInputActive ? null : (
                                <span className="placeholder">Введіть текст або </span>
                            )}
                            {isInputActive ? null : (
                                <label className="area-label">
                                    завантажте файл
                                    <input
                                        className="area-input"
                                        type="file"
                                        onChange={handleFileChange}
                                        accept=".doc, .docx, application/msword, application/vnd.openxmlformats-officedocument.wordprocessingml.document, .csv, application/vnd.openxmlformats-officedocument.spreadsheetml.sheet, application/vnd.ms-excel, application/msword, application/vnd.ms-excel, application/vnd.ms-powerpoint, text/plain, application.pdf, .rtf, .txt, .pdf, .zip"
                                    />
                                </label>
                            )}
                        </div>
                    </div>
{/* ---------------------------------------------------------------------------------------------------------------- */}
                    <fieldset className='text_field email'></fieldset>
                    <fieldset className='text_field name'></fieldset>
                    <fieldset className='text_field comment'></fieldset>
                    <fieldset className='button-checkbox'>
                        <legend className={`${isOpenedLanguage ? 'checked' : ''}`}>{label}</legend>
                        <div className="button-checkbox" ref={languageRef}>
                            <button
                                className={`custom-button ${isOpenedLanguage ? 'checked' : ''} ${selectedColor}`}
                                onClick={handleButtonClickLanguage}
                                type="button"
                            >
                                <label>
                                    {selectedLanguage}
                                    <img alt="icon" src="/arrow_down.svg"/>
                                </label>
                            </button>
                            <div className={`hidden-list ${isOpenedLanguage ? 'active' : ''}`}>
                                <div className="position-list">
                                    <label onClick={() => handleListOptionClickLanguage('Редагування', "color")}>Редагування</label>
                                    <label onClick={() => handleListOptionClickLanguage('Переклад', "color")}>Переклад</label>
                                </div>
                            </div>
                        </div>
                    </fieldset>
                </div>
                <div className="makeOrder_item"></div>
            </form>
        </main>
    );
}

export default App;
