import "./styles/App.css";
import MyCheckbox from "./components/MyCheckbox/MyCheckbox";
import MyArea from "./components/MyArea/MyArea";
import {MyClipBoard} from "./components/MyClipBoard/MyClipBoard";
import {useState} from "react";

function App() {

    const [opened, isOpened] = useState(false)
    const [defaultValue, setDefaultValue] = useState('Послуга')

    const clickBtnOpener = () => {
        isOpened(!opened)
    }

    return (
        <main className="main_new">
            <form className="section_new makeOrder">
                <div className="makeOrder_item inputs">
                    <h3 className="medium_title">Замовити переклад або редагування</h3>
                    <label htmlFor="" className="box-btn">
                        <button className={`btn first ${opened ? 'opener' : ''} `} type="button" onClick={clickBtnOpener}>
                            <label htmlFor="" className="box-title">
                                <span>
                                    {defaultValue}
                                </span>
                                <img src="/arrow_down.svg" alt="arrow-down"/>
                            </label>
                        </button>
                        <button className="btn two" type="button" id="btn-two">
                            <img src="/arrow_down.svg" alt="arrow-down"/>
                        </button>
                    </label>
                </div>
                <div className="makeOrder_item">
                </div>
            </form>
        </main>
    )
}

export default App;
