import "./styles/App.css";
import React, {useState} from "react";

function App() {

    const [opened, setOpened] = useState(false);

    return (
        <main className="main_new">
            <form className="section_new makeOrder">
                <div className="makeOrder_item inputs">
                    <h3 className="medium_title">Замовити переклад або редагування</h3>
                    {opened && (
                        <button type="button" className="makeOrder_item selector" onClick={() => setOpened(!opened)}>
                            <label>{opened ? 'Послуги' : 'Закрити'}</label>
                        </button>
                    )}
                    {!opened && (
                        <button type="button" className="makeOrder_item selector opened" onClick={() => setOpened(!opened)}>
                            <label>{opened ? 'Закрити' : 'Відкрити'}</label>
                        </button>
                    )}


                </div>
                <div className="makeOrder_item">
                </div>
            </form>
        </main>
    )
}

export default App;
