import React, {useState} from 'react';
import './MyArea.css';

const MyArea = () => {

    const [isFocused, setIsFocused] = useState(false);
    const [text, setText] = useState("");

    const handleFocus = () => {
        setIsFocused(true);
    };

    const handleBlur = () => {
        setIsFocused(false);
    };

    const handleChange = (e) => {
        setText(e.target.value);
    };

    return (
        <div className="area">
            <textarea className="area-text" onFocus={handleFocus} onBlur={handleBlur} onChange={handleChange} value={text}>
                {isFocused || text ? null : <div className="placeholder">Введіть текст або</div>}
                {/*<div className="area-download">*/}

                {/*</div>*/}
            </textarea>
        </div>
    );
};

export default MyArea;
