import React, {useState} from 'react';
import './MyArea.css';

const MyArea = () => {

    // const [defaultTextArea, setDefaultTextArea] = useState("");
    //
    // const handleTextChange = (event) => {
    //     setDefaultTextArea(event.target.value);
    // };

    return (
        <div className="area">
            <textarea
                className="area-text"
                placeholder="Замовити переклад або редагування"
            >
                <div>
                    <span></span>
                    <label>
                        <input className="input" type="file"/>
                    </label>
                </div>
            </textarea>
        </div>
    );
};

export default MyArea;
