import {useState} from "react";

function ButtonWithHighlight() {
    const [isHighlighted, setIsHighlighted] = useState(false);
}

export function SelectList() {
    const handleButtonClick = () => {
        // При нажатии на кнопку изменяем состояние isHighlighted
        setIsHighlighted(!isHighlighted);
    };
    const buttonStyle = {
        backgroundColor: isHighlighted ? 'yellow' : 'initial', // Желтый фон при выделении
        // Другие стили кнопки
    };
    const [isOpen, setOpen] = useState(false)
    return (
        <div>
            <button className="selector" onClick={() => setOpen(!isOpen)} type="button">
                <label>
                    Послуги
                </label>
                <nav className={`menu ${isOpen ? "active" : ""}`}>
                    <ul className="menu_list">
                        <li className="menu_item">Редагування</li>
                        <li className="menu_item">Переклад</li>
                    </ul>
                </nav>
            </button>
        </div>
    )
}
