import React, { useState, useRef, useEffect } from 'react';
import './MyCheckbox.css';

const MyCheckbox = () => {
    const [isChecked1, setIsChecked1] = useState(false);
    const [selectedValue1, setSelectedValue1] = useState("Кнопка-чекбокс 1");
    const [selectedColor1, setSelectedColor1] = useState("");
    const [isChecked2, setIsChecked2] = useState(false);
    const [selectedValue2, setSelectedValue2] = useState("Кнопка-чекбокс 2");
    const [selectedColor2, setSelectedColor2] = useState("");
    const containerRef1 = useRef(null);
    const containerRef2 = useRef(null);

    const handleButtonClick1 = () => {
        setIsChecked1(!isChecked1);
    };

    const handleButtonClick2 = () => {
        setIsChecked2(!isChecked2);
    };

    const handleListOptionClick1 = (value, color) => {
        setSelectedValue1(value);
        setSelectedColor1(color);
        setIsChecked1(false);
        setIsChecked2(true);
    };

    const handleListOptionClick2 = (value, color) => {
        setSelectedValue2(value);
        setSelectedColor2(color);
        setIsChecked2(false);
    };

    const handleClickOutside1 = (event) => {
        if (containerRef1.current && !containerRef1.current.contains(event.target)) {
            setIsChecked1(false);
        }
    };

    const handleClickOutside2 = (event) => {
        if (containerRef2.current && !containerRef2.current.contains(event.target)) {
            setIsChecked2(false);
        }
    };

    useEffect(() => {
        document.addEventListener('click', handleClickOutside1);
        document.addEventListener('click', handleClickOutside2);

        return () => {
            document.removeEventListener('click', handleClickOutside1);
            document.removeEventListener('click', handleClickOutside2);
        };
    }, []);

    const options2 = isChecked1
        ? ["Значение 3", "Значение 4"]
        : isChecked2
            ? ["Значение 5", "Значение 6"]
            : [];

    return (
        <div className="button-container">
            <div className="button-checkbox" ref={containerRef1}>
                <button
                    className={`custom-button ${isChecked1 ? 'checked' : ''} ${selectedColor1}`}
                    onClick={handleButtonClick1}
                    type="button"
                >
                    {selectedValue1}
                </button>
                <div className={`hidden-list ${isChecked1 ? 'active' : ''}`}>
                    <div onClick={() => handleListOptionClick1("Значение 1", "checked")}>Значение 1</div>
                    <div onClick={() => handleListOptionClick1("Значение 2", "checked-red")}>Значение 2</div>
                </div>
            </div>

            <div className="button-checkbox" ref={containerRef2}>
                <button
                    className={`custom-button ${isChecked2 ? 'checked' : ''} ${selectedColor2}`}
                    onClick={handleButtonClick2}
                    disabled={!isChecked1}
                    type="button"
                >
                    {selectedValue2}
                </button>
                <div className={`hidden-list ${isChecked2 ? 'active' : ''}`}>
                    {options2.map((option, index) => (
                        <div key={index} onClick={() => handleListOptionClick2(option, "checked")}>
                            {option}
                        </div>
                    ))}
                </div>
            </div>
        </div>
    );
};

export default MyCheckbox;
